import React from 'react';
import { Link } from 'react-router-dom';

function CustomerPage() {
  return (
    <div style={{ minHeight: '100vh', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #e0e7ff 0%, #f8fafc 100%)', position: 'relative' }}>
      <Link
        to="/admin"
        style={{
          position: 'absolute',
          top: 24,
          right: 32,
          color: '#1976d2',
          fontWeight: 600,
          textDecoration: 'none',
          fontSize: 16,
          background: 'rgba(255,255,255,0.7)',
          padding: '6px 16px',
          borderRadius: 8,
          boxShadow: '0 2px 8px rgba(99,102,241,0.08)',
          transition: 'background 0.2s, color 0.2s',
        }}
        onMouseOver={e => (e.target.style.background = '#e3f2fd')}
        onMouseOut={e => (e.target.style.background = 'rgba(255,255,255,0.7)')}
      >
        Admin Login
      </Link>
      <h1 style={{ fontSize: '2.5rem', fontWeight: 800, color: '#1a237e', marginBottom: 24, textShadow: '0 2px 8px rgba(0,0,0,0.08)' }}>
        Environmental Monitoring System
      </h1>
      <div style={{ background: 'rgba(255,255,255,0.85)', borderRadius: 20, boxShadow: '0 8px 32px 0 rgba(99,102,241,0.13)', padding: 32, minWidth: 320, textAlign: 'center' }}>
        <h2 style={{ color: '#1976d2', fontWeight: 700, marginBottom: 16 }}>Live Environmental Data</h2>
        <p style={{ color: '#333', fontSize: 18 }}>
          (Weather, air quality, and other environmental data will be displayed here.)
        </p>
      </div>
    </div>
  );
}

export default CustomerPage;
