import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import AirQualityPanel from './AirQualityPanel';
import WaterQualityPanel from './WaterQualityPanel';
import WeatherForecastPanel from './WeatherForecastPanel';

function CustomerPage() {
  const [selected, setSelected] = useState(null);

  return (
    <div style={{
      minHeight: '100vh',
      width: '100vw',
      position: 'relative',
      background: `linear-gradient(135deg, rgba(30,58,138,0.7) 0%, rgba(2,132,199,0.5) 100%), url('https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=1200&q=80')`,
      backgroundSize: 'cover',
      backgroundPosition: 'center',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'flex-start',
      paddingBottom: 64,
      paddingTop: 0,
    }}>
      <Link
        to="/admin"
        style={{
          position: 'absolute',
          top: 24,
          right: 32,
          color: '#1976d2',
          fontWeight: 600,
          textDecoration: 'none',
          fontSize: 16,
          background: 'rgba(255,255,255,0.7)',
          padding: '6px 16px',
          borderRadius: 8,
          boxShadow: '0 2px 8px rgba(99,102,241,0.08)',
          transition: 'background 0.2s, color 0.2s',
          zIndex: 2,
        }}
        onMouseOver={e => (e.target.style.background = '#e3f2fd')}
        onMouseOut={e => (e.target.style.background = 'rgba(255,255,255,0.7)')}
      >
        Admin Login
      </Link>
      <div style={{
        marginTop: 56,
        marginBottom: 32,
        background: 'rgba(255,255,255,0.25)',
        borderRadius: 32,
        boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.25)',
        padding: '32px 24px 24px 24px',
        maxWidth: 900,
        width: '95vw',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        backdropFilter: 'blur(10px) saturate(180%)',
        WebkitBackdropFilter: 'blur(10px) saturate(180%)',
        border: '1.5px solid rgba(255,255,255,0.25)',
      }}>
        <h1 style={{
          fontSize: '2.7rem',
          fontWeight: 900,
          color: '#fff',
          marginBottom: 8,
          textShadow: '0 4px 16px rgba(30,58,138,0.18), 0 1px 2px rgba(255,255,255,0.5)',
          letterSpacing: 1.5,
          textAlign: 'center',
        }}>
          Environmental Monitoring System
        </h1>
        <p style={{
          color: '#e0e7ff',
          fontSize: 20,
          marginBottom: 24,
          textAlign: 'center',
          textShadow: '0 2px 8px rgba(30,58,138,0.18)',
        }}>
          Real-time air, water, and weather data for your environment
        </p>
        <div style={{ width: '100%', maxWidth: 800 }}>
          <AirQualityPanel
            selected={selected === 'air'}
            onClick={() => setSelected(selected === 'air' ? null : 'air')}
          />
          <hr style={{ border: 'none', borderTop: '2px solid #e0e7ff', margin: '32px 0' }} />
          <WaterQualityPanel
            selected={selected === 'water'}
            onClick={() => setSelected(selected === 'water' ? null : 'water')}
          />
          <hr style={{ border: 'none', borderTop: '2px solid #e0e7ff', margin: '32px 0' }} />
          <WeatherForecastPanel
            selected={selected === 'weather'}
            onClick={() => setSelected(selected === 'weather' ? null : 'weather')}
          />
        </div>
      </div>
    </div>
  );
}

export default CustomerPage;
