import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, RadialBarChart, RadialBar
} from 'recharts';

function WaterQualityPanel() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      const { data, error } = await supabase
        .from('water_quality')
        .select('*')
        .order('recorded_at', { ascending: true })
        .limit(100);
      if (!error) setData(data || []);
      setLoading(false);
    };
    fetchData();
  }, []);

  if (loading) return <div>Loading water quality data...</div>;
  if (!data.length) return <div>No water quality data available.</div>;

  // Prepare data for pH gauge (latest value)
  const latest = data[data.length - 1];
  const phGaugeData = [
    { name: 'pH', value: latest.ph, fill: latest.ph < 6.5 || latest.ph > 8.5 ? '#d32f2f' : '#388e3c' }
  ];

  return (
    <div style={{ margin: '32px 0', background: 'rgba(255,255,255,0.9)', borderRadius: 16, boxShadow: '0 2px 8px rgba(99,102,241,0.08)', padding: 24 }}>
      <h2 style={{ color: '#1976d2', fontWeight: 700, marginBottom: 12 }}>Water Quality</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: 32, justifyContent: 'center' }}>
        {/* pH Gauge */}
        <div style={{ width: 200, height: 200, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
          <RadialBarChart width={200} height={200} cx={100} cy={100} innerRadius={70} outerRadius={100} barSize={18} data={phGaugeData} startAngle={180} endAngle={0} >
            <RadialBar minAngle={15} background clockWise dataKey="value" />
            <text x={100} y={110} textAnchor="middle" dominantBaseline="middle" fontSize={32} fontWeight={700} fill="#222">{latest.ph}</text>
            <text x={100} y={150} textAnchor="middle" fontSize={16} fill="#1976d2">pH</text>
          </RadialBarChart>
        </div>
        {/* Trends Chart */}
        <div style={{ minWidth: 350, width: 500, height: 220 }}>
          <ResponsiveContainer width="100%" height={220}>
            <LineChart data={data} margin={{ top: 16, right: 24, left: 0, bottom: 0 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="recorded_at" tickFormatter={str => str.slice(11, 16)} minTickGap={20} />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="ph" stroke="#1976d2" dot={false} name="pH" />
              <Line type="monotone" dataKey="turbidity" stroke="#388e3c" dot={false} name="Turbidity" />
              <Line type="monotone" dataKey="dissolved_oxygen" stroke="#ffa726" dot={false} name="Dissolved O₂" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
}

export default WaterQualityPanel;
