import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { supabase } from './supabaseClient';
import AirQualityPanel from './AirQualityPanel';
import WaterQualityPanel from './WaterQualityPanel';
import WeatherForecastPanel from './WeatherForecastPanel';
import './index.css';
import './LoginPage.css';

function CustomerPage() {
  const [selected, setSelected] = useState(null);
  // Data and loading state for all panels
  const [airData, setAirData] = useState([]);
  const [airLoading, setAirLoading] = useState(true);
  const [waterData, setWaterData] = useState([]);
  const [waterLoading, setWaterLoading] = useState(true);
  const [weatherData, setWeatherData] = useState([]);
  const [weatherLoading, setWeatherLoading] = useState(true);

  useEffect(() => {
    const fetchAir = async () => {
      setAirLoading(true);
      const { data, error } = await supabase
        .from('air_quality')
        .select('*')
        .order('recorded_at', { ascending: true })
        .limit(100);
      if (!error) setAirData(data || []);
      setAirLoading(false);
    };
    fetchAir();
  }, []);

  useEffect(() => {
    const fetchWater = async () => {
      setWaterLoading(true);
      const { data, error } = await supabase
        .from('water_quality')
        .select('*')
        .order('recorded_at', { ascending: true })
        .limit(100);
      if (!error) setWaterData(data || []);
      setWaterLoading(false);
    };
    fetchWater();
  }, []);

  useEffect(() => {
    const fetchWeather = async () => {
      setWeatherLoading(true);
      const { data, error } = await supabase
        .from('weather_forecast')
        .select('*')
        .order('forecast_time', { ascending: true })
        .limit(100);
      if (!error) setWeatherData(data || []);
      setWeatherLoading(false);
    };
    fetchWeather();
  }, []);

  // Helper to render the selected panel in modal
  const renderModalPanel = () => {
    let PanelComponent = null;
    let panelProps = {};
    if (selected === 'air') {
      PanelComponent = AirQualityPanel;
      panelProps = { selected: true, onClick: () => {}, data: airData, loading: airLoading };
    }
    if (selected === 'water') {
      PanelComponent = WaterQualityPanel;
      panelProps = { selected: true, onClick: () => {}, data: waterData, loading: waterLoading };
    }
    if (selected === 'weather') {
      PanelComponent = WeatherForecastPanel;
      panelProps = { selected: true, onClick: () => {}, data: weatherData, loading: weatherLoading };
    }
    if (!PanelComponent) return null;
    return (
      <div className="modal-overlay" onClick={() => setSelected(null)}>
        <div className="modal-content" onClick={e => e.stopPropagation()}>
          <button
            onClick={() => setSelected(null)}
            style={{ position: 'absolute', top: 18, right: 18, background: 'rgba(30,58,138,0.08)', border: 'none', borderRadius: '50%', width: 36, height: 36, fontSize: 24, color: '#222', cursor: 'pointer', boxShadow: '0 2px 8px rgba(30,58,138,0.08)', transition: 'background 0.2s', zIndex: 1004 }}
            aria-label="Close"
          >
            ×
          </button>
          <PanelComponent {...panelProps} />
        </div>
      </div>
    );
  };

  return (
    <div className="login-bg">
      {/* Animated background blobs */}
      <div className="login-blob login-blob1" />
      <div className="login-blob login-blob2" />
      <div className="login-blob login-blob3" />
      {/* Overlay and content */}
      {selected && <div className="modal-overlay" />}
      <Link
        to="/admin"
        style={{ position: 'absolute', top: 24, right: 32, color: '#1976d2', fontWeight: 600, textDecoration: 'none', fontSize: 16, background: 'rgba(255,255,255,0.7)', padding: '6px 16px', borderRadius: 8, boxShadow: '0 2px 8px rgba(99,102,241,0.08)', transition: 'background 0.2s, color 0.2s', zIndex: 2 }}
        onMouseOver={e => (e.target.style.background = '#e3f2fd')}
        onMouseOut={e => (e.target.style.background = 'rgba(255,255,255,0.7)')}
      >
        Admin Login
      </Link>
      <div className="glass-panel" style={{ marginTop: 32, marginBottom: 24, filter: selected ? 'blur(6px) grayscale(0.2)' : 'none', pointerEvents: selected ? 'none' : 'auto' }}>
        <h1 className="text-center">Environmental Monitoring System</h1>
        <p className="text-center" style={{ color: '#e0e7ff', fontSize: 18, marginBottom: 12, textShadow: '0 2px 8px rgba(30,58,138,0.18)' }}>
          Real-time air, water, and weather data for your environment
        </p>
        <div className="panels-row">
          <div className="panel-col">
            <AirQualityPanel
              selected={selected === 'air'}
              onClick={() => setSelected(selected === 'air' ? null : 'air')}
              data={airData}
              loading={airLoading}
            />
          </div>
          <div className="panel-col">
            <WaterQualityPanel
              selected={selected === 'water'}
              onClick={() => setSelected(selected === 'water' ? null : 'water')}
              data={waterData}
              loading={waterLoading}
            />
          </div>
          <div className="panel-col">
            <WeatherForecastPanel
              selected={selected === 'weather'}
              onClick={() => setSelected(selected === 'weather' ? null : 'weather')}
              data={weatherData}
              loading={weatherLoading}
            />
          </div>
        </div>
      </div>
      {/* Modal Panel */}
      {selected && (
        <div className="modal-overlay" onClick={() => setSelected(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <button
              onClick={() => setSelected(null)}
              style={{ position: 'absolute', top: 18, right: 18, background: 'rgba(30,58,138,0.08)', border: 'none', borderRadius: '50%', width: 36, height: 36, fontSize: 24, color: '#222', cursor: 'pointer', boxShadow: '0 2px 8px rgba(30,58,138,0.08)', transition: 'background 0.2s', zIndex: 1004 }}
              aria-label="Close"
            >
              ×
            </button>
            {selected === 'air' && <AirQualityPanel selected={true} onClick={() => {}} data={airData} loading={airLoading} />}
            {selected === 'water' && <WaterQualityPanel selected={true} onClick={() => {}} data={waterData} loading={waterLoading} />}
            {selected === 'weather' && <WeatherForecastPanel selected={true} onClick={() => {}} data={weatherData} loading={weatherLoading} />}
          </div>
        </div>
      )}
      {/* Modal pop-in animation */}
      <style>{`
        @keyframes modalPopIn {
          0% { transform: scale(0.92); opacity: 0.2; }
          60% { transform: scale(1.06); opacity: 1; }
          100% { transform: scale(1.12); opacity: 1; }
        }
      `}</style>
    </div>
  );
}

export default CustomerPage;
