import React, { useState } from 'react';
import { createClient } from '@supabase/supabase-js';
import './LoginPage.css';

// TODO: Replace with your actual Supabase project URL and anon key
const SUPABASE_URL = 'https://pnpabkmytgxxlfhkhbdg.supabase.co';
const SUPABASE_ANON_KEY = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6InBucGFia215dGd4eGxmaGtoYmRnIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NTEyNjQ3MTMsImV4cCI6MjA2Njg0MDcxM30.sv0zS35FeiPnpgmnjjkjSmsnmE9vwZMgMVEosPggz1U';
const supabase = createClient(SUPABASE_URL, SUPABASE_ANON_KEY);

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);
    const { error } = await supabase.auth.signInWithPassword({ email, password });
    setLoading(false);
    if (error) {
      setError(error.message);
    } else {
      setSuccess(true);
    }
  };

  return (
    <div className="login-bg">
      {/* Animated background blobs */}
      <div className="login-blob login-blob1" />
      <div className="login-blob login-blob2" />
      <div className="login-blob login-blob3" />
      <div className="login-card">
        <h2 className="login-title">Sign In</h2>
        <form onSubmit={handleLogin}>
          <div className="login-field">
            <label className="login-label">Email</label>
            <input
              className="login-input"
              type="email"
              value={email}
              onChange={e => setEmail(e.target.value)}
              required
            />
          </div>
          <div className="login-field">
            <label className="login-label">Password</label>
            <input
              className="login-input"
              type="password"
              value={password}
              onChange={e => setPassword(e.target.value)}
              required
            />
          </div>
          <button
            className="login-btn"
            type="submit"
            disabled={loading}
          >
            {loading ? (
              <span className="login-btn-loading">
                <span className="login-spinner"></span>
                Logging in...
              </span>
            ) : 'Login'}
          </button>
          {error && <div className="login-error">{error}</div>}
          {success && <div className="login-success">Login successful!</div>}
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
