import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';
import Papa from 'papaparse';

function EditableTable({ tableName, columns, canEdit }) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editIdx, setEditIdx] = useState(null);
  const [editRow, setEditRow] = useState({});
  const [adding, setAdding] = useState(false);
  const [addRow, setAddRow] = useState({});
  const [csvError, setCsvError] = useState(null);
  const [csvSuccess, setCsvSuccess] = useState(null);

  useEffect(() => {
    fetchData();
    // eslint-disable-next-line
  }, [tableName]);

  const fetchData = async () => {
    setLoading(true);
    setError(null);
    const { data, error } = await supabase.from(tableName).select('*').order('forecast_time', { ascending: false });
    if (error) setError('Failed to fetch data.');
    setData(data || []);
    setLoading(false);
  };

  const startEdit = idx => {
    setEditIdx(idx);
    setEditRow({ ...data[idx] });
  };

  const cancelEdit = () => {
    setEditIdx(null);
    setEditRow({});
  };

  const saveEdit = async idx => {
    const row = editRow;
    const { id, ...updateFields } = row;
    const { error } = await supabase.from(tableName).update(updateFields).eq('id', id);
    if (error) {
      setError('Failed to update row.');
    } else {
      const newData = [...data];
      newData[idx] = row;
      setData(newData);
      setEditIdx(null);
      setEditRow({});
    }
  };

  const deleteRow = async id => {
    const { error } = await supabase.from(tableName).delete().eq('id', id);
    if (error) {
      setError('Failed to delete row.');
    } else {
      setData(data.filter(row => row.id !== id));
    }
  };

  const startAdd = () => {
    setAdding(true);
    setAddRow({});
  };

  const cancelAdd = () => {
    setAdding(false);
    setAddRow({});
  };

  const saveAdd = async () => {
    const { error, data: inserted } = await supabase.from(tableName).insert([addRow]).select();
    if (error) {
      setError('Failed to add row.');
    } else {
      setData([...data, ...(inserted || [])]);
      setAdding(false);
      setAddRow({});
    }
  };

  const handleCsvUpload = e => {
    setCsvError(null);
    setCsvSuccess(null);
    const file = e.target.files[0];
    if (!file) return;
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: async results => {
        const csvRows = results.data;
        // Only keep fields that match columns
        const validRows = csvRows.map(row => {
          const filtered = {};
          columns.forEach(col => {
            if (row[col]) filtered[col] = row[col];
          });
          return filtered;
        });
        // Check if at least one row has all required columns
        const allFieldsPresent = validRows.every(row => columns.every(col => col in row));
        if (!allFieldsPresent) {
          setCsvError('CSV headers must match all required columns.');
          return;
        }
        // Insert into Supabase
        const { error } = await supabase.from(tableName).insert(validRows);
        if (error) {
          setCsvError('Failed to import CSV.');
        } else {
          setCsvSuccess('CSV imported successfully!');
          fetchData();
        }
      },
      error: err => setCsvError('Failed to parse CSV.'),
    });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div style={{ maxWidth: 900, margin: '0 auto' }}>
      {canEdit && (
        <div style={{ marginBottom: 12 }}>
          <label style={{ fontWeight: 500 }}>
            Upload CSV: <input type="file" accept=".csv" onChange={handleCsvUpload} />
          </label>
          {csvError && <span style={{ color: 'red', marginLeft: 8 }}>{csvError}</span>}
          {csvSuccess && <span style={{ color: 'green', marginLeft: 8 }}>{csvSuccess}</span>}
        </div>
      )}
      <table style={{ width: '100%', borderCollapse: 'collapse', background: 'rgba(255,255,255,0.95)', borderRadius: 8, overflow: 'hidden' }}>
        <thead>
          <tr>
            {columns.map(col => (
              <th key={col} style={{ borderBottom: '2px solid #e0e7ff', padding: 8, textAlign: 'left', background: '#f1f5fa' }}>{col}</th>
            ))}
            {canEdit && <th style={{ background: '#f1f5fa' }}>Actions</th>}
          </tr>
        </thead>
        <tbody>
          {data.map((row, idx) => (
            <tr key={row.id || idx}>
              {columns.map(col => (
                <td key={col} style={{ borderBottom: '1px solid #e0e7ff', padding: 8 }}>
                  {editIdx === idx ? (
                    <input
                      value={editRow[col] || ''}
                      onChange={e => setEditRow({ ...editRow, [col]: e.target.value })}
                      style={{ width: '100%', padding: 4 }}
                    />
                  ) : (
                    row[col]
                  )}
                </td>
              ))}
              {canEdit && (
                <td style={{ borderBottom: '1px solid #e0e7ff', padding: 8 }}>
                  {editIdx === idx ? (
                    <>
                      <button onClick={() => saveEdit(idx)} style={{ marginRight: 8 }}>Save</button>
                      <button onClick={cancelEdit}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button onClick={() => startEdit(idx)} style={{ marginRight: 8 }}>Edit</button>
                      <button onClick={() => deleteRow(row.id)} style={{ color: 'red' }}>Delete</button>
                    </>
                  )}
                </td>
              )}
            </tr>
          ))}
          {canEdit && adding && (
            <tr>
              {columns.map(col => (
                <td key={col}>
                  <input
                    value={addRow[col] || ''}
                    onChange={e => setAddRow({ ...addRow, [col]: e.target.value })}
                    style={{ width: '100%', padding: 4 }}
                  />
                </td>
              ))}
              <td>
                <button onClick={saveAdd} style={{ marginRight: 8 }}>Add</button>
                <button onClick={cancelAdd}>Cancel</button>
              </td>
            </tr>
          )}
        </tbody>
      </table>
      {canEdit && !adding && (
        <button onClick={startAdd} style={{ marginTop: 12 }}>Add New Row</button>
      )}
    </div>
  );
}

export default EditableTable;
